import React, { Component } from 'react';
import DateTimePicker from 'react-datetime-picker';
import Select from 'react-select';
// TODO: refactor the code using useState
export default class Create extends Component {
  constructor(props) {
    super(props);
    this.onChangeName = this.onChangeName.bind(this);
    this.onChangeTime = this.onChangeTime.bind(this);
    this.onChangeZoom = this.onChangeZoom.bind(this);
    this.onChangePurpose = this.onChangePurpose.bind(this);
    this.state = {
      name: '',
      time: new Date(),
      zoom: '',
      purpose: '',
    };
  }
  onChangeName(e) {
    this.setState({
      name: e.target.value,
    });
  }
  onChangeTime(e) {
    console.log(e);
    this.setState({
      time: e,
    });
  }
  onChangeZoom(e) {
    this.setState({
      zoom: e.target.value,
    });
  }
  onChangePurpose(e) {
    console.log(e);
    this.setState({
      purpose: e.value,
    });
  }
  onSubmit(e) {
    e.preventDefault();
    const form = {
      name: this.state.name,
      time: this.state.time,
      zoom: this.state.zoom,
      purpose: this.state.purpose,
    };
    console.log(form);
    window.location = '/';
  }
  render() {
    const options = [
      { value: 'pair-programming', label: 'Pair Programming Algo' },
      { value: 'interview-prep', label: 'Interview Prep' },
      { value: 'chat', label: 'Chat' },
    ];
    return (
      <div>
        <h3>Create Card</h3>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Name:</label>
            <input
              type="text"
              className="form-control"
              value={this.state.name}
              onChange={this.onChangeName}
            />
          </div>
          <div className="form-group">
            <label>Time:</label>
            <br />
            <DateTimePicker
              onChange={this.onChangeTime}
              value={this.state.time}
            />
          </div>
          <div className="form-group">
            <label>Zoom:</label>
            <input
              type="text"
              className="form-control"
              value={this.state.zoom}
              onChange={this.onChangeZoom}
            />
          </div>
          <div className="form-group">
            <label>Purpose:</label>
            <Select
              options={options}
              value={this.state.name}
              onChange={this.onChangePurpose}
              defaultValue={options[0]}
              isRtl={true}
            />
          </div>
        </form>
      </div>
    );
  }
}
